// 1.导包
import Vue from 'vue'
import VueRouter from 'vue-router'

// 2.调用函数,把 VueRouter安装为 Vue的插件
Vue.use(VueRouter)

// 3.创建路由实例对象
const router = new VueRouter({
  routes: [
    { path: '/home', component: Home },
    { path: '/movie', component: Movie },
    { path: '/about', component: About },
  ],
})

// 4.向外共享路由实例对象
export default router
