import Header from '@/components/Header.vue';
import Footer from '@/components/Footer.vue';
import Item from '@/components/Item.vue';
import EventBus from './components/EventBus';
export default {
  data() {
    return {
      itemLists: [{
        id: 1,
        status: true,
        src: '...',
        info: '商品1',
        price: 1.99,
        count: 1
      }, {
        id: 2,
        status: false,
        src: '...',
        info: '商品2',
        price: 1.00,
        count: 1
      }, {
        id: 3,
        status: true,
        src: '...',
        info: '商品3',
        price: 1.89,
        count: 1
      }],
      // 结算商品总数量
      itemsCount: 0,
      // 结算商品总金额
      itemsCost: 0,
      // 结算商品类型数量(几种商品 -> 用来计算是否全选   computed计算属性最优)
      itemCount: 0
    };
  },
  components: {
    Header,
    Item,
    Footer
  },
  methods: {
    // item组件中单个商品状态切换反馈至App根组件中 更新商品列表信息
    changeItemStatusOrCount(id, status, count) {
      this.itemLists[id - 1].status = status;
      this.itemLists[id - 1].count = count;
    },
    // 页面刚加载时便计算已选定商品类型数量、商品数量、商品总额传递给Footer组件
    AppToFooter() {
      // 商品数量
      this.itemsCount = this.itemLists.filter(item => item.status).reduce((itemsCount, item) => {
        return itemsCount + item.count;
      }, 0);
      // 商品总额
      this.itemsCost = this.itemLists.filter(item => item.status).reduce((itemsCost, Item) => {
        return itemsCost + Item.count * Item.price;
      }, 0).toFixed(2) - '0';
      // 商品类型数量
      this.itemCount = this.itemLists.filter(item => item.status).length;
    }
  },
  updated() {
    //函数复用
    this.AppToFooter();
    //兄弟组件传递信息(其实是父子组件-ref只能传递初始值)
    EventBus.$emit("AppToFooter", this.itemsCount, this.itemsCost, this.itemCount);
  },
  created() {
    //函数复用
    this.AppToFooter();
  }
};
