import Header from '@/components/Header.vue';
import Footer from '@/components/Footer.vue';
import Item from '@/components/Item.vue';
import EventBus from './components/EventBus';
export default {
  data() {
    return {
      itemLists: [{
        id: 1,
        status: true,
        src: '...',
        info: '商品1',
        price: 1.99,
        count: 1
      }, {
        id: 2,
        status: true,
        src: '...',
        info: '商品2',
        price: 1.00,
        count: 1
      }, {
        id: 3,
        status: true,
        src: '...',
        info: '商品3',
        price: 1.89,
        count: 1
      }],
      // 结算商品类型数量(几种商品 -> 用来计算是否全选)
      itemStatusAll: false
    };
  },
  components: {
    Header,
    Item,
    Footer
  },
  computed: {
    itemsCount() {
      return this.itemLists.filter(item => item.status).reduce((itemsCount, item) => {
        return itemsCount + item.count;
      }, 0);
    },
    itemsCost() {
      return this.itemLists.filter(item => item.status).reduce((itemsCost, Item) => {
        return itemsCost + Item.count * Item.price;
      }, 0).toFixed(2) - '0';
    }
  },
  methods: {
    // item组件中单个商品状态切换反馈至App根组件中 更新商品列表itemLists信息
    ItemToApp(id, status, count) {
      this.itemLists[id - 1].status = status;
      this.itemLists[id - 1].count = count;
    }
    // 页面刚加载时便计算已选定商品类型数量传递给Footer组件
    // AppToFooter() {
    //     // 商品类型是否全选
    //     this.itemStatusAll = this.itemLists.every(item => item.status)
    // }
  },
  updated() {
    // //函数复用
    // this.AppToFooter()
    // //兄弟组件传递信息(其实是根子组件-ref只能传递初始值)
    // EventBus.$emit("AppToFooter", this.itemStatusAll)
    EventBus.$emit("AppToFooter", this.itemLists.every(item => item.status));
  },
  created() {
    //函数复用
    // this.AppToFooter()
    EventBus.$emit("AppToFooter", this.itemLists.every(item => item.status));
  }
};
